INSERT INTO LOCATION (locationName, image, description) VALUES ('The Blue Mosque', 'blue-mosque.jpg', 'Built from 1609 to 1616, during the rule of Sultan Ahmed I. It contains a tomb of the founder, a madrasah and a hospice. The Sultan Ahmed Mosque is still popularly used as a mosque. The mosque is popularly known as the Blue Mosque for the blue tiles adorning the walls of its interior.')
INSERT INTO LOCATION (locationName, image, description) VALUES ('Hagia Sophia Museum', 'hagia-sophia', 'Hagia Sophia, Turkish Ayasofya, Latin Sancta Sophia, also called Church of the Holy Wisdom or Church of the Divine Wisdom, cathedral built at Constantinople (now Istanbul, Turkey) in the 6th century ce (532–537) under the direction of the Byzantine emperor Justinian I.')